mapper.insert:
insert into
hamburger_table
side_menu_table
drink_table
dessert_table
values ( 0, #{menu_name}, #{price}, #{sales}, #{kcal}, #{image},
#{burger_type},
-1,
1, 0
#{size}, 0, 2, #{only_mc_morning_flag}
#{size}, #{drink_type}, 3, #{only_mc_morning_flag}
4, #{only_mc_morning_flag}
)

mapper.select:
SELECT 0 AS id, '' as menu_type, '' AS menu_name, 0 as price, 0 as kcal, '' AS size, '' as image, 0 as hamburger_category_code, 0 as mc_lunch_flag, 0 as set_menu_flag, 0 as only_mc_morning_flag, (select COUNT(*) from hamburger_table ht LEFT OUTER JOIN menu_category_table mct ON(mct.id = ht.menu_category_code)
where mct.id = #{menu_type}
)
+
(select COUNT(*) from side_menu_table smt LEFT OUTER JOIN menu_category_table mct ON(mct.id = smt.menu_category_code)
where mct.id = #{menu_type}
)
+
(select COUNT(*) from drink_table dt LEFT OUTER JOIN menu_category_table mct ON(mct.id = dt.menu_category_code)
where mct.id = #{menu_type}
)
+
(select COUNT(*) from dessert_table dt LEFT OUTER JOIN menu_category_table mct ON(mct.id = dt.menu_category_code)
where mct.id = #{menu_type}
) AS total_count
union
(SELECT ht.id, mct.menu_category_name as menu_type, ht.hamburger_name AS menu_name, ht.price, ht.kcal, '' AS size, ht.image, ht.hamburger_category_code, ht.mc_lunch_flag, 0 as set_menu_flag, 0 as only_mc_morning_flag, 0 FROM hamburger_table ht LEFT OUTER JOIN menu_category_table mct ON(mct.id = ht.menu_category_code)
where mct.id = #{menu_type}
UNION
SELECT smt.id, mct.menu_category_name as menu_type, smt.side_menu_name AS menu_name, smt.price, smt.kcal, if(smt.size like 'none', '', smt.size) AS size, smt.image, 0 as hamburger_category_code, 0 as mc_lunch_flag, smt.set_menu_flag, smt.only_mc_morning_flag, 0 FROM side_menu_table smt LEFT OUTER JOIN menu_category_table mct ON(mct.id = smt.menu_category_code)
where mct.id = #{menu_type}
UNION
SELECT dt.id, mct.menu_category_name as menu_type, dt.drink_name AS menu_name, dt.price, dt.kcal, if(dt.size like 'none', '', dt.size) AS size, dt.image, 0 as hamburger_category_code, 0 as mc_lunch_flag, dt.set_menu_flag, dt.only_mc_morning_flag, 0 FROM drink_table dt LEFT OUTER JOIN menu_category_table mct ON(mct.id = dt.menu_category_code)
where mct.id = #{menu_type}
UNION
SELECT dt.id, mct.menu_category_name as menu_type, dt.dessert_name AS menu_name, dt.price, dt.kcal, '' AS size, dt.image, 0 as hamburger_category_code, 0 as mc_lunch_flag, 0 as set_menu_flag, dt.only_mc_morning_flag, 0 FROM dessert_table dt LEFT OUTER JOIN menu_category_table mct ON(mct.id = dt.menu_category_code)
where mct.id = #{menu_type}
LIMIT #{index}, 10);

mapper.update:
UPDATE
hamburger_table
side_menu_table
drink_table
dessert_table
SET price = #{price}, sales = #{sales}, kcal = #{kcal},
image = #{image},
size = #{size},
hamburger_name = #{menu_name}, mc_lunch_flag = #{mc_lunch_flag}, hamburger_category_code = #{hamburger_category_code}
side_menu_name = #{menu_name}, set_menu_flag = #{set_menu_flag}, only_mc_morning_flag = #{only_mc_morning_flag}, mc_morning_flag = #{is_mc_morning_flag}
drink_name = #{menu_name}, drink_category_code = #{drink_category_code}, set_menu_flag = #{set_menu_flag}, only_mc_morning_flag = #{only_mc_morning_flag}, mc_morning_flag = #{is_mc_morning_flag}
dessert_name = #{menu_name}, only_mc_morning_flag = #{only_mc_morning_flag}, mc_morning_flag = #{is_mc_morning_flag}
WHERE id = #{id}

mapper.select:
SELECT id, hamburger_name as menu_name, price, kcal, image, mc_lunch_flag FROM hamburger_table WHERE
hamburger_category_code = #{hamburger_category_code}
hamburger_category_code != -1
;

SELECT id, 'single' AS set_size, hamburger_name, kcal, image, price FROM hamburger_table WHERE id = #{id}
union
SELECT ht.id, 'M' AS set_size, ht.hamburger_name, ht.kcal + dt.kcal + smt.kcal AS set_kcal, image, if((tt.start_time < NOW() and tt.end_time > NOW()) and ht.mc_lunch_flag = 1, ht.price + dt.price + smt.price - 500 , ht.price + dt.price + smt.price) AS price FROM hamburger_table ht left outer join time_table tt ON (1 = 1) LEFT OUTER JOIN (select price, kcal from drink_table where drink_name LIKE '코카콜라 제로' AND size LIKE 'M') dt ON(1 = 1) LEFT OUTER JOIN (select price, kcal from side_menu_table where side_menu_name LIKE '감자튀김' AND size LIKE 'M') smt ON(1 = 1) WHERE ht.id = #{id} AND tt.id = 2
UNION
SELECT ht.id, 'L' AS set_size, ht.hamburger_name, ht.kcal + dt.kcal + smt.kcal AS set_kcal, image, if((tt.start_time < NOW() and tt.end_time > NOW()) and ht.mc_lunch_flag = 1, ht.price + dt.price + smt.price - 500 , ht.price + dt.price + smt.price) AS price FROM hamburger_table ht LEFT OUTER JOIN time_table tt ON(1 = 1) LEFT OUTER JOIN (select price, kcal from drink_table where drink_name LIKE '코카콜라 제로' AND size LIKE 'L') dt ON(1 = 1) LEFT OUTER JOIN (select price, kcal from side_menu_table where side_menu_name LIKE '감자튀김' AND size LIKE 'L') smt ON(1 = 1) WHERE ht.id = #{id} AND tt.id = 2;

SELECT id,
if(size LIKE 'none', side_menu_name, concat(side_menu_name, ' - ', if(size LIKE 'M', '미디엄', '라지'))) AS menu_name, smt.price - smt_p.price AS price, smt.price as default_price,
dessert_name AS menu_name, price,
if(size LIKE 'none', drink_name, concat(drink_name, ' - ', if(size LIKE 'M', '미디엄', '라지'))) AS menu_name, dt.price - dt_p.price AS price, dt.price as default_price,
kcal, image FROM
side_menu_table smt LEFT OUTER JOIN (select price from side_menu_table where side_menu_name LIKE '감자튀김' and size = #{size}) smt_p ON (1 = 1)
dessert_table
drink_table dt LEFT OUTER JOIN (select price from drink_table where drink_name LIKE '코카콜라' and size = #{size}) dt_p ON (1 = 1)
WHERE only_mc_morning_flag = #{mc_morning_flag}
and size LIKE #{size}
and set_menu_flag = 1
and drink_category_code = 2
and drink_category_code = 1
;

choose.when:
SELECT smt.id, if(size LIKE 'none', smt.side_menu_name, concat(smt.side_menu_name, ' - ', if(smt.size LIKE 'M', '미디엄', '라지'))) AS menu_name, if(smt.price - smt2.price < 0, 0, smt.price - smt2.price) AS price, smt.price as default_price, smt.kcal, smt.image FROM side_menu_table smt LEFT OUTER JOIN(select price from side_menu_table where side_menu_name LIKE '감자튀김' and size LIKE #{size}) smt2 ON(1 = 1) WHERE set_menu_flag = 1 and size LIKE #{size}
and smt.only_mc_morning_flag = 1
and smt.only_mc_morning_flag = 0
;

mapper.select:
SELECT * FROM (SELECT id, hamburger_name as menu_name, price, sales, kcal, image, menu_category_code, hamburger_category_code FROM hamburger_table
UNION
SELECT id, dessert_name as menu_name, price, sales, kcal, image, menu_category_code, 0 FROM dessert_table ORDER BY sales desc LIMIT 0 ,6) ranking_table WHERE sales > 0 and hamburger_category_code != -1;
